using System;using System.Collections.Generic;using System.Linq;using System.Text;using Android.App;using Android.Content;using Android.OS;using Android.Runtime;using Android.Views;using Android.Widget;namespace GroceryListApp {    [Activity(Label = "AboutActivity")]    public class AboutActivity : Activity {        protected override void OnCreate(Bundle savedInstanceState) {            base.OnCreate(savedInstanceState);            SetContentView(Resource.Layout.About);            FindViewById<Button>(Resource.Id.btnLearnMore).Click += OnLearnMoreClick;        }        public void OnLearnMoreClick(object sender, EventArgs e) {            Intent intent = new Intent();            intent.SetAction(Intent.ActionView);            intent.SetData(Android.Net.Uri.Parse("http://www.xamarin.com"));            StartActivity(intent);        }    }}