using System;using System.Collections.Generic;using System.Linq;using System.Text;using Android.App;using Android.Content;using Android.OS;using Android.Runtime;using Android.Views;using Android.Widget;namespace GroceryListApp {    [Activity(Label = "AddActivity")]    public class AddActivity : Activity {        EditText etName;        EditText etCount;        protected override void OnCreate(Bundle savedInstanceState) {            base.OnCreate(savedInstanceState);            SetContentView(Resource.Layout.Add);            etName = FindViewById<EditText>(Resource.Id.etName);            etCount = FindViewById<EditText>(Resource.Id.etCount);            FindViewById<Button>(Resource.Id.btnSave).Click += OnSaveClicked;            FindViewById<Button>(Resource.Id.btnCancel).Click += OnCancelClicked;        }        public void OnSaveClicked(object sender, EventArgs e) {            string name = etName.Text;            int count = int.Parse(etCount.Text);            var intent = new Intent();            intent.PutExtra("ItemName", name);            intent.PutExtra("ItemCount", count);            SetResult(Result.Ok, intent);            Finish();        }        public void OnCancelClicked(object sender, EventArgs e) {            Finish();        }    }}