using System;using System.Collections.Generic;using System.Linq;using System.Text;using Android.App;using Android.Content;using Android.OS;using Android.Runtime;using Android.Views;using Android.Widget;namespace GroceryListApp {    [Activity(Label = "DetailsActivity")]    public class DetailsActivity : Activity {        TextView tvName;        TextView tvCount;        protected override void OnCreate(Bundle savedInstanceState) {            base.OnCreate(savedInstanceState);            SetContentView(Resource.Layout.Details);            int position = Intent.GetIntExtra("ItemPosition", -1);            var item = MainActivity.ItemList[position];            if (item != null) {                tvName = FindViewById<TextView>(Resource.Id.tvName);                tvCount = FindViewById<TextView>(Resource.Id.tvCount);                tvName.Text = item.Name;                tvCount.Text = item.Count.ToString();            }        }    }}