using System;using System.Collections.Generic;using System.Linq;using System.Text;using Android.App;using Android.Content;using Android.OS;using Android.Runtime;using Android.Views;using Android.Widget;namespace GroceryListApp {    [Activity(Label = "ItemsActivity")]    public class ItemsActivity : Activity {        protected override void OnCreate(Bundle savedInstanceState) {            base.OnCreate(savedInstanceState);            SetContentView(Resource.Layout.Items);            var myListView = FindViewById<ListView>(Resource.Id.lvGroceryList);            myListView.Adapter = new ArrayAdapter<Item>(this, Android.Resource.Layout.SimpleListItem1, Android.Resource.Id.Text1, MainActivity.ItemList);            myListView.ItemClick += OnItemClick;        }        public void OnItemClick(object sender, AdapterView.ItemClickEventArgs e) {            var intent = new Intent(this, typeof(DetailsActivity));            intent.PutExtra("ItemPosition", e.Position); // e.Position is the position in the list of the item the use touched            StartActivity(intent);        }    }}