using Android.App;using Android.Widget;using Android.OS;using System;using Android.Content;using System.Collections.Generic;namespace GroceryListApp {    [Activity(Label = "GroceryListApp", MainLauncher = true, Icon = "@mipmap/icon")]    public class MainActivity : Activity {        Button btnAbout;        Button btnItems;        Button btnAdd;        // Pre-Load data on inital app OnCreate (speed)        public static List<Item> ItemList = new List<Item>();        protected override void OnCreate(Bundle savedInstanceState) {            // Pre-Load data            ItemList.Add(new Item("Milk", 5));            ItemList.Add(new Item("Crackers", 3));            ItemList.Add(new Item("Apples", 4));            base.OnCreate(savedInstanceState);            SetContentView(Resource.Layout.Main);            btnItems = FindViewById<Button>(Resource.Id.btnItems);            btnAbout = FindViewById<Button>(Resource.Id.btnAbout);            btnAdd = FindViewById<Button>(Resource.Id.btnAdd);            btnItems.Click += ItemsIntent;            btnAbout.Click += AboutIntent;            btnAdd.Click += AddIntent;        }        public void ItemsIntent(object sender, EventArgs e) {            Intent intent = new Intent(this, typeof(ItemsActivity));            StartActivity(intent);        }        public void AboutIntent(object sender, EventArgs e) {            Intent intent = new Intent(this, typeof(AboutActivity));            StartActivity(intent);        }        public void AddIntent(object sender, EventArgs e) {            Intent intent = new Intent(this, typeof(AddActivity));            //            // The AddItem Activity will return the Name and Count of the newly added item.            // Use StartActivityForResult so you are notified when the AddItem Activity completes.            // The parameter '100' is the request code that lets you identify which Activity is sending you results;            // the value '100' is arbitrary, it has no meaning to Android.            //            StartActivityForResult(intent, 100);        }        protected override void OnActivityResult(int requestCode, Result resultCode, Intent data) {            //            // A requestCode of 100 indicates this result is from the AddItem Activity.            // A resultCode of OK means the user touched the SAVE button and not the CANCEL button.            // The values for the new item are stored in the Intent Extras.            //            if (requestCode == 100 && resultCode == Result.Ok) {                string name = data.GetStringExtra("ItemName");                int count = data.GetIntExtra("ItemCount", 0);                MainActivity.ItemList.Add(new Item(name, count));            }        }     }}